dt-example.css:
#events {
	margin-bottom: 1em;
	padding: 1em;
	background-color: #f6f6f6;
	border: 1px solid #999;
	border-radius: 3px;
	height: 100px;
	overflow: auto;
}

dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		select: true
	} );

	table
		.on( 'user-select', function ( e, dt, type, cell, originalEvent ) {
			if ( $(originalEvent.target).index() === 0 ) {
				e.preventDefault();
			}
		} );
} );
