dt-example.js:
var events = $('#events');
var table = $('#example').DataTable({
	select: true
});

table
	.on('select', function (e, dt, type, indexes) {
		var rowData = table
			.rows(indexes)
			.data()
			.toArray();

		events.prepend(
			'<div><b>' +
				type +
				' selection</b> - ' +
				JSON.stringify(rowData) +
				'</div>'
		);
	})
	.on('deselect', function (e, dt, type, indexes) {
		var rowData = table
			.rows(indexes)
			.data()
			.toArray();

		events.prepend(
			'<div><b>' +
				type +
				' <i>de</i>selection</b> - ' +
				JSON.stringify(rowData) +
				'</div>'
		);
	});

dt-example.js-vanilla:
function message(message) {
	let el = document.querySelector('#events');
	let div = document.createElement('div');

	div.innerHTML = message;
	el.prepend(div);
}

let table = new DataTable('#example', {
	select: true
});

table
	.on('select', function (e, dt, type, indexes) {
		let rowData = table
			.rows(indexes)
			.data()
			.toArray();

		message('<b>' + type + ' selection</b> - ' + JSON.stringify(rowData));
	})
	.on('deselect', function (e, dt, type, indexes) {
		let rowData = table
			.rows(indexes)
			.data()
			.toArray();

		message(
			'<b>' + type + ' <i>de</i>selection</b> - ' + JSON.stringify(rowData)
		);
	});
