dt-example.js:
$('#example').DataTable({
	ajax: '../../../../examples/server_side/scripts/ids-objects.php',
	columns: [
		{ data: 'name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'extn' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	processing: true,
	rowId: 'id',
	select: true,
	serverSide: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: '../../../../examples/server_side/scripts/ids-objects.php',
	columns: [
		{ data: 'name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'extn' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	processing: true,
	rowId: 'id',
	select: true,
	serverSide: true
});
